INSERT INTO `coffeeblend`.`product` (`product_name`, `description`, `price`, `type`, `status`) VALUES ('Cà phê Arabica', 'Cà phê Arabica có hai loại đang được nước ta trồng đó là Catimor và Moka. Moka có mùi thơm quyến rũ, vị nhạt; còn Catimor có mùi thơm nồng nàn, vị hơi chua.', '50000', 'Cà phê', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `description`, `price`, `type`, `status`) VALUES ('Cà phê Culi', 'Một loại cà phê khác của nước ta có hạt tròn to bóng mẩy, đặc biệt so với các loại cafe khác là nó chỉ có một hạt duy nhất trong một trái. Cà phê culi vị đắng ngắt, hương thơm say lòng người, nước đen sóng sánh.', '40000', 'Cà phê', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `description`, `price`, `type`, `status`) VALUES ('Cà phê Cherry (cà phê mít)', '. Cà phê Cherry dậy lên mùi hương thoang thoảng khi pha và có vị chua của Cherry tạo ra một cảm hứng thật sảng khoái. Loại cafe này phù hợp với sở thích của phái nữ vừa tạo một cảm giác dân dã vừa sang trọng.', '45000', 'Cà phê', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Americano', 'https://product.hstatic.net/1000075078/product/americano_copy_92414ac8e0634fb48ea72b21bc496b43_large.jpg', 'Americano được pha chế bằng cách thêm nước vào một hoặc hai shot Espresso để pha loãng độ đặc của cà phê, từ đó mang lại hương vị nhẹ nhàng, không gắt mạnh và vẫn thơm nồng nàn.','Cà phê', 39000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Cappuccino', 'https://product.hstatic.net/1000075078/product/cappuccino_af58b143de7e4faeb9a3ce59b313c946_master.jpg', 'Cappuccino được gọi vui là thức uống \"một-phần-ba\" - 1/3 Espresso, 1/3 Sữa nóng, 1/3 Foam.','Cà phê', 45000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Caramel Macchiato', 'https://product.hstatic.net/1000075078/product/caramel_macchiato_24ff98b746e84b7aba702c01a60b1a52_master.jpg', 'Vị thơm béo của bọt sữa và sữa tươi, vị đắng thanh thoát của cà phê Espresso hảo hạng, và vị ngọt đậm của sốt caramel.','Cà phê', 55000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Espresso', 'https://product.hstatic.net/1000075078/product/espressonong_f60fc6f25baa404c8a8bfc40c396f27e_large.jpg', 'Một cốc Espresso nguyên bản được bắt đầu bởi những hạt Arabica chất lượng, phối trộn với tỉ lệ cân đối hạt Robusta, cho ra vị ngọt caramel, vị chua dịu và sánh đặc. Để đạt được sự kết hợp này, chúng tôi xay tươi hạt cà phê cho mỗi lần pha.','Cà phê', 35000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Espresso Tonic', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQr7o-OFLFHSJYbPdWxxskE5tfYPw6WcCOn7g&usqp=CAU', 'Đậm đà, tươi mới Sự kết hợp mới lạ hiện đại với 2 shot Espresso và lớp Tonic dưới cùng - Cho từng ngụm đậm đà, chua thanh, đắng dịu, hậu vị tươi mới đầy tươi tỉnh.','Cà phê', 55000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Latte', 'https://product.hstatic.net/1000075078/product/latte_dd6427d058294df5aa4745e5a6035a93_large.jpg', 'Khi chuẩn bị Latte, cà phê Espresso và sữa nóng được trộn lẫn vào nhau, bên trên vẫn là lớp foam nhưng mỏng và nhẹ hơn Cappucinno.','Cà phê', 45000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mocha', 'https://product.hstatic.net/1000075078/product/mocha_da_a2913936f9d84d07b63ac1ddf5dd2d0f_large.jpg', 'Cà phê Mocha được ví von đơn giản là Sốt Sô cô la được pha cùng một tách Espresso.','Cà phê', 49000,'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Yuzu espresso tonic', 'https://salt.tikicdn.com/cache/w444/ts/product/38/e0/82/8bc7f2e5e795cd02653ca8fd005519cf.jpg', 'Khác biệt - Tươi tỉnh - Giải nhiệt Sự kết hợp lần đầu tiên của Espresso, Tonic cùng nước ép Yuzu (quýt Nhật) - Cho bạn 1 tách Espresso khác biệt chưa từng có, đậm vị tươi tỉnh, giải nhiệt khoan khoái không thể hoà lẫn.','Cà phê', 55000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('BẠC XỈU', 'https://product.hstatic.net/1000075078/product/bac_siu_099c749a7d6044bc9f7f0eac7f57808f_large.jpg', 'Theo chân những người gốc Hoa đến định cư tại Sài Gòn, Bạc sỉu là cách gọi tắt của \"Bạc tẩy sỉu phé\" trong tiếng Quảng Đông, chính là: Ly sữa trắng kèm một chút cà phê.','Cà phê', 32000, 'Bình thường');
