INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Cà phê Lúa Mạch đá', 'https://product.hstatic.net/1000075078/product/merry-coffee_1x1_77bfcd5059c040faade5b105dfb623d1_master.jpg', 'Đắm chìm vào hương vị cà phê mới mẻ khi kết hợp cùng lúa mạch thơm ngon. Không quá đắng lại ngọt dịu dễ ghiền. Thưởng thức ngay nhé.','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Cà phê Lúa Mạch nóng', 'https://product.hstatic.net/1000075078/product/merry-coffee-nong-1x1_17712e23293145de8fc98d2be06405fc_master.jpg', '1 tách cà phê đặc biệt cho mùa đặc biệt nhất năm. Lúa mạch thơm lừng kết hợp cùng cà phê đượm vị. Thưởng thức vị Giáng sinh dành riêng cho bạn hôm nay nhé.','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('COLD BREW TRUYỀN THỐNG', 'https://product.hstatic.net/1000075078/product/classic_cold_brew_67a26b702dbc4602b36c450893efdab0_master.jpg', 'Trong một năm trở lại đây, cà phê pha lạnh Cold Brew đã trở thành một xu hướng thưởng thức mới đối với các tín đồ cà phê Việt Nam. Cách pha chế Cold Brew tưởng chừng như đơn giản nhưng thực chất lại đòi hỏi rất nhiều sự tinh tế, cẩn trọng của người barista trong từng khâu thực hiện.  Mất 24 tiếng để ngâm ủ cà phê trong nước lạnh, Cold Brew mang đến sự mượt mà và tinh tế trong hương vị. Tại The Coffee House, Cold Brew được ủ và phục vụ mỗi ngày từ 100% hạt Arabica Cầu Đất với hương gỗ thông, hạt dẻ, nốt sô-cô-la đặc trưng, thoang thoảng hương khói nhẹ giúp Cold Brew giữ nguyên vị tươi mới','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('COLD BREW PHÚC BỒN TỬ', 'https://product.hstatic.net/1000075078/product/cold_brew_pnt_copy_172c6bdd6923422c9a01111867ce8686_master.jpg', 'Một sự kết hợp đầy thuyết phục cho những người thích cà phê nhưng lại muốn thay đổi vị. Vị chua ngọt của trái phúc bồn tử, làm dậy lên hương vị trái cây tự nhiên vốn sẵn có trong hạt cà phê, hòa quyện thêm vị đăng đắng, ngọt dịu nhẹ nhàng của Cold Brew để mang đến một cách thưởng thức cà phê hoàn toàn mới, vừa thơm lừng hương cà phê quen thuộc, vừa nhẹ nhàng và thanh mát bởi hương trái cây đầy thú vị.','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('COLD BREW SỮA TƯƠI', 'https://product.hstatic.net/1000075078/product/cold_brew_sua_tuoi_ee783daa0b44466b858f990f42943d46_master.jpg', 'Thanh mát và cân bằng với hương vị cà phê nguyên bản 100% Arabica Cầu Đất cùng sữa tươi thơm béo cho từng ngụm tròn vị, hấp dẫn.','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('COLD BREW CAM SẢ', 'https://product.hstatic.net/1000075078/product/cold_brew_cs_49d3bb2e8c1e482580f9d0160251a5c3_master.jpg', 'Tinh tế trong hương - sắc - vị','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('CÀ PHÊ ĐÁ XAY', 'https://product.hstatic.net/1000075078/product/cf_da_xay_c062933b9a044c5f9b6370a34e2d2216_master.jpg', 'Một phiên bản "upgrade" từ ly cà phê sữa quen thuộc, nhưng lại đầy tỉnh táo và tươi mát hơn bởi lớp đá xay mát lạnh đi kèm. Nhấp 1 ngụm là thấy đã, ngụm thứ hai thêm tỉnh táo và cứ thế lôi cuốn bạn đến giọt cuối cùng.','Cà phê', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố Bơ', 'https://kangaroovietnam.vn/Uploads/sinh-to-bo.jpg', 'Bơ là một loại trái cây bổ dưỡng, có lợi cho hệ tiêu hóa...  đặc biệt là cực kỳ tốt cho sự phát triển của thai nhi giúp khỏe mạnh và thông minh', 'Sinh tố', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố chuối vị Socola', 'https://kangaroovietnam.vn/Uploads/sinh-to-chuoi-socola.png', 'Sinh tố chuối vị lạ mà ngon, uống cực kỳ tốt cho hệ tiêu hóa, đặc biệt có thể dùng để giảm cân, vì có chứa  chất trytophan kích thích giấc ngủ ngon và cải thiện sự suy nhược của cơ thể.', 'Sinh tố', '34000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố cam sữa chua', 'https://kangaroovietnam.vn/Uploads/sinh-to-cam-sua-chua.png', 'Cam và sữa chua đều có chứa các loại vitamin cần thiết cho cơ thể, đặc biệt với khả năng làm đẹp an toàn và hiệu quả, ít tốn kém. Các loại sinh tố ngon dễ làm kết hợp giữa cam và sữa chua sẽ giúp bạn có được một thức uống tuyệt hảo cho những ngày hè nóng thêm tươi mát và dễ chịu hơn.', 'Sinh tố', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố nho', 'https://kangaroovietnam.vn/Uploads/sinh-to-nho.jpg', 'Nho có nhiều dưỡng chất giúp tăng sức đề kháng, chống bệnh tật, hạn chế quá trình lão hóa, ngăn ngừa ung thư, tim mạch, giải độc bảo vệ cơ thể. Ngoài ăn ngon cả quả ra, có thể làm sinh tố ngon để uống mỗi ngày', 'Sinh tố', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố chuối', 'https://kangaroovietnam.vn/Uploads/sinh-to-chuoi.jpg', 'Sinh tố chuối có khả năng cải thiện hệ tiêu hóa, cung cấp năng lượng, kiểm soát lượng cholesterol hiệu quả, chống béo phì, giảm cân, lấy lại vóc dáng thon gọn cho người phụ nữ.', 'Sinh tố', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố Dưa hấu', 'https://kangaroovietnam.vn/Uploads/sinh-to-dua-hau.jpg', 'Dưa hấu là loại trái cây có khả năng chữa trị viêm thận và làm hạ huyết áp, vì khả năng làm chuyển hóa chất men trong dưa hấu thành protein không hòa tan thành protein hòa tan, tăng cường dưỡng chất cho người viêm thận, loại đường tổng hợp giúp hạ huyết áp. Đặc biệt, dưa hấu có khả năng làm đẹp da, bảo vệ da do có chứa nhiều vitamin A, B6, C giúp chị em phụ nữ có được da đẹp, dáng chuẩn.', 'Sinh tố', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố dứa', 'https://kangaroovietnam.vn/Uploads/sinh-to-dua.jpg', 'Sinh tố dứa được ưa chuộng vào mùa hè, do có dưa hấu có vị mát, thơm nồng của mùi sữa chua. Uống sinh tố dưa hấu giúp đẹp da, tăng sức đề kháng được nhiều người lựa chọn để giải nhiệt vào mùa hè.', 'Sinh tố', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố đậu xanh rau má', 'https://kangaroovietnam.vn/Uploads/sinh-to-rau-ma.jpg', 'Rau má và đậu xanh có thể kết hợp thành các loại sinh tố ngon, lành tính với sức khỏe. Sinh tố đậu xanh rau má có thể vừa giúp bạn giải nhiệt, thanh mát mà còn giúp người uống giảm cân. Ngoài ra, trong đậu xanh còn chứa nhiều vitamin, khoáng chất giúp tăng cường trao đổi chất giúp cơ thể có nhiều năng lượng tích cực hơn.', 'Sinh tố', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố dâu tây', 'https://kangaroovietnam.vn/Uploads/sinh-to-dau-tay.jpg', 'Dâu tây có chứa hàm lượng vitamin C và đường fructose cao. Vị của dâu tây cực kỳ hấp dẫn, nên đây sẽ là thức uống giải khát được nhiều người yêu thích vào mùa hè.', 'Sinh tố', '34000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố cà chua', 'https://kangaroovietnam.vn/Uploads/sinh-to-ca-chua.jpg', 'Cà chua có thể làm cho vóc dáng thon gọn, làm đẹp da, giảm cân an toàn. Vì bên trong cà chua có chứa một lượng lớn canxi và vitamin K dồi dào cho xương chắc khỏe và cũng là dưỡng chất giúp mái tóc khỏe đẹp hơn. Ngoài ra, cà chua có chứa một lượng axit citric, thúc đẩy quá trình chuyển hóa đường và đốt cháy chất béo giúp ngăn ngừa lão hóa cho da.', 'Sinh tố', '34000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố Cà rốt, Bí đỏ', 'https://kangaroovietnam.vn/Uploads/sinh-to-ca-rot.jpg', 'Cà rốt có khả năng làm đẹp, giảm cân rất hiệu quả. Ngoài ăn bình thường, có thể xay cà rốt thành các loại sinh tố hoa quả để uống giải nhiệt trong những ngày mùa hè nắng nóng.', 'Sinh tố', '33000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Sinh tố mít', 'https://kangaroovietnam.vn/Uploads/sinh-to-mit.jpg', 'Mít là loại trái cây có khả năng điều hòa huyết áp, tim mạch, cải thiện hệ tiêu hóa, cái thiện hệ tiêu hóa, tình trạng táo bón. Sinh tố mít uống cho vị ngon, hấp dẫn là lựa chọn không thể bỏ qua vào mùa hè nắng nóng.', 'Sinh tố', '31000', 'Bình thường');
