INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('TRÀ OOLONG VẢI', 'https://product.hstatic.net/1000075078/product/tra_vai_046084306a484f41bd8c37efaab8f538_master.jpg', 'Là thức uống "bắt vị" được lấy cảm hứng từ trái Vải - thức quả tròn đầy, quen thuộc trong cuộc sống người Việt - kết hợp cùng Trà Oolong làm từ những lá trà tươi hảo hạng. Ly Trà Oolong Vải Như Ý là mong ước năm mới sắp tới sẽ là một năm tràn đầy những điều ngọt ngào, như ý với tất cả mọi người.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('TRÀ OOLONG HẠT SEN', 'https://product.hstatic.net/1000075078/product/tra_sen_bd7ab23f9be048429bb13682f6515ec3_master.jpg', 'Sự kết hợp của Trà Oolong hương thơm nhẹ, vị nồng hậu cùng Hạt sen tươi mềm có vị ngọt, sáp, vừa ngon miệng vừa có tác dụng an thần, tốt cho cơ thể. Sẽ thật tuyệt vời nếu bạn mời những người thân trong gia đình mình một tách Trà Oolong Sen An Nhiên thay cho lời tri ân và lời chúc năm mới an nhiên tràn sức khoẻ. Đặc biệt, lớp kem sữa phô mai được phủ lên bề mặt ly sẽ cho bạn một trải nghiệm cân bằng hơn về hương vị.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('TRÀ ĐÀO CAM SẢ', 'https://product.hstatic.net/1000075078/product/tra_dao_5f3925d9bfca4d0abc17f95b05fff5f7_master.jpg', 'Vị thanh ngọt của đào Hy Lạp, vị chua dịu của Cam Vàng nguyên vỏ, vị chát của trà đen tươi được ủ mới mỗi 4 tiếng, cùng hương thơm nồng đặc trưng của sả chính là điểm sáng làm nên sức hấp dẫn của thức uống này. Sản phẩm hiện có 2 phiên bản Nóng và Lạnh phù hợp cho mọi thời gian trong năm.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('TRÀ OOLONG BƯỞI MẬT ONG', 'https://product.hstatic.net/1000075078/product/tra_buoi_5c4c5ce2d4e44042a069ec9011ef1a9f_master.jpg', 'Lớp trà oolong đã man mát, nhưng sẽ vị càng thêm vị khi có hương bưởi cùng những tép bưởi mọng nước đi kèm - cho bạn 1 cách thưởng thức đầy thú vị. Cùng đánh thức vị giác, khởi động 1 ngày tươi tắn cùng hương vị mới lạ này nhé!','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('TRÀ OOLONG PHÚC BỒN TỬ', 'https://product.hstatic.net/1000075078/product/tra_pbt_b1d7b2aac4474bad9e16906964170a17_master.jpg', 'Một sự kết hợp đầy hoàn hảo giữa thanh mát và bổ dưỡng. Lần đầu tiên Trà Oolong và trái "Phúc Bồn Tử" hoàn toàn tự nhiên, được các barista của chúng tôi kết hợp một cách tinh tế để tạo ra một dư vị hoàn toàn tươi mới. Nhấp ngay một ngụm là thấy mát lạnh ngay tức khắc, đọng lại mãi nơi cuốn họng là hương vị trà thơm lừng và vị ngọt thanh, chua dịu khó quên của trái phúc bồn tử.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà chanh', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-chanh-ngon.jpg', 'Trà chanh có vị chua mát, ngọt thanh và hơi chát của trà giúp giải nhiệt và thanh lọc cơ thể. Trong quả chanh chứa nhiều hàm lượng vitamin C giúp cung cấp độ ẩm cho da và giúp da trắng hồng, mịn màng hơn. ','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà kiwi', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-trai-cay-kiwi.jpg', 'Nếu nhắc đến trà trái cây được yêu thích thì không thể bỏ qua trà Kiwi. Chỉ cần nhìn ly trà xanh kiwi là bạn đã cảm nhận được sự tươi mát rồi. Đây là sự lựa chọn tuyệt vời cho những bạn yêu thích vị chua ngọt của trái kiwi bổ dưỡng. Thường xuyên uống trà kiwi sẽ giúp bạn ngày càng trẻ đẹp và cung cấp thêm nhiều dưỡng chất cho cơ thể.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà trái cây nhiệt đới', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-trai-cay-nhiet-doi-1.jpg', 'Trà trái cây nhiệt đới là là sự kết hợp của các loại quả phổ biến như: thanh long, xoài, lê, đào, táo, dưa hấu,… kết hợp với trà túi lọc và một ít đường cát trắng. Trà có hương vị tươi mát của nhiều loại trái cây, vị ngọt của đường và hơi chát của trà. Những ngày hè oi nóng, vừa được uống trà và ăn trái cây tươi ngon thì còn gì tuyệt vời hơn đúng không','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà táo bạc hà', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-tao-bac-ha.jpg', 'Điểm đặc biệt của trà táo bạc hà là sự kết hợp the mát của bạc hà và vị ngọt dịu của táo, tạo nên mùi rất đặc biệt và dễ uống. Ngoài ra, trong táo giàu chất xơ, giúp tăng cảm giác no, rất tốt cho những người muốn giảm cân.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà lựu trân châu ', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-luu-tran-chau.jpg', 'Trà lựu có màu đẹp mắt, hương vị thơm mát kết hợp với trân châu giòn ngon thật tuyệt cho những ngày hè nóng bức đúng không?  Quả lựu có nhiều vitamin và khoáng chất giúp trẻ hóa làn da, cho làn da phái đẹp luôn căng hồng. ','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà xoài ', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-xoai-kem-cheese.jpg', 'Quả xoài được mệnh danh là “vua trái cây” vì hương vị thơm ngon, có giá trị dinh dưỡng cao như: vitamin A, B, C,…. giúp giải nhiệt tốt cho mùa hè. Trà xoài còn là đồ uống khá nhiều bạn trẻ yêu thích bởi vị ngọt dịu và chua nhẹ đặc trưng của quả xoài. Bên cạnh đó trà xoài còn giúp các bạn gái chống lão hóa và giúp chăm sóc da hiệu quả.','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà vải bạc hà', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-vai-bac-ha.jpg', 'Sự kết hợp độc đáo của vải thơm ngọt với bạc hà mát lạnh sảng khoái tạo nên hương vị đặc biệt cho thức uống trà vải bạc hà. Với vị ngọt thơm của vải và mát lạnh, the the của bạc hà đã tạo nên thức uống vô cùng cuốn hút với rất nhiều người. Vừa được thưởng thức nước trà thơm mát, vừa được ăn những trái vải tươi giòn ngon, thật tuyệt đúng không?','Trà', 32000, 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà trái cây đào', 'https://kinhdoanhcafe.com/wp-content/uploads/2019/07/tra-dao-cam-sa-2.jpg', 'Trà đào đơn giản là sự kết hợp giữa trà và đào. Trà đào được yêu thích bởi vị ngọt thanh, bổ dưỡng và giá khá “hạt rẻ”. Những miếng đào chín vừa tới nhai giòn giòn sần sật kết hợp vị trà hơi chan chát hòa quyện tạo vị chua chua, ngọt ngọt rất dễ chịu và dễ uống.','Trà', 32000, 'Bình thường');
