INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa trân châu trắng', 'https://jarvis.vn/uploaded/tra-sua-tran-chau-trang(2).jpg', 'Trà sữa trân châu thường có 2 loại là trân châu đen và trân châu trắng. Nếu trân châu đen đã quá phổ biến và quen thuộc với nhiều người thì trân châu trắng là một loại topping khá mới lạ. Trân châu trắng được làm từ bột rau câu dẻo, bột rau câu giòn, tạo hình thành các hạt tròn bé có màu trắng.Trân châu trắng có mùi thơm, vị ngọt và giòn giòn, không dẻo như trân châu đen.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Oreo Cake Cream', 'https://jarvis.vn/uploaded/tra-sua-oreo-cake-cream(1).jpg', 'Trong số các loại trà sữa thì trà sữa Oreo Cake Cream là một loại trà uống khá mới lạ với nhiều người. Trà sữa Oreo Cake Cream về cơ bản là trà sữa thông thường, tuy nhiên không dùng bột pha trực tiếp, tạo nên vị trà thơm, chát cùng chút béo của sữa. Tiếp theo, trà sữa Oreo Cake Cream được phủ thêm một lớp cake cream vàng nhạt phía trên và xung quanh thành cốc. Những miếng bánh Oreo thơm ngon, hấp dẫn được nghiền nhỏ và rắc vụn lên phía trên lớp kem', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa trân châu đường đen', 'https://jarvis.vn/uploaded/tra-sua-tran-chau-duong-den(1).jpg', 'Trà sữa trân châu đường đen vẫn gồm nguyên liệu là như trà sữa thông thường. Thông thường trà sữa trân châu đường đen sẽ sử dụng sữa tươi không đường để vị không bị ngọt quá và phù hợp với phần topping gồm trân châu và đường đen. Một điểm nữa khiến giới trẻ mê mẩn trà sữa trân châu đường đen so với các loại trà sữa khác đó là vẻ ngoài bắt mắt của cốc trà với phần trà sữa trắng hòa quyện khéo léo cùng phần đường đen, trân châu đen bên dưới.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà xoài kem cheese', 'https://jarvis.vn/wp-content/uploads/2019/05/tra-xoai-kem-cheese-1.png', 'Một trong những món đồ uống của các tín đồ kem cheese là trà xoài kem cheese. Trà xoài kem cheese được làm từ trà, xoài tươi cùng với phần kem cheese, tạo nên loại thức uống độc đáo có vị thơm đặc trưng của xoài, chút vị chua ngọt cùng với lớp kem béo béo, mặn mặn hấp dẫn nhiều người.Trà xoài kem cheese có vị ngậy, béo và chút mặn của kem cheese, vị ngọt đậm của xoài tươi, 2 nguyên liệu này kết hợp và làm tăng thêm vị ngon riêng của mỗi loại nguyên liệu, khiến người uống không còn cảm thấy kem cheese quá ngậy hay xoài quá ngọt. Độ ngậy, ngọt và mùi thơm vừa phải.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa khoai môn', 'https://jarvis.vn/uploaded/tra-sua-khoai-mon(1).jpg', 'Trà sữa khoai môn tuy không phải là thức uống mới nhưng nó vẫn giữ được độ hot với giới trẻ, đặc biệt là vào các khoảng thời gian hè nắng nóng. Trà sữa khoai môn có vị béo béo của sữa, mùi thơm của khoai môn cùng với chút vị chát nhẹ của trà. Thức uống này không quá ngọt và ngậy như những loại trà sữa có kem cheese, vị ngọt thanh mà vẫn có chút béo bùi là điểm cộng lớn nhất của trà sữa khoai môn. Trà sữa khoai môn một số nơi sẽ dùng bột khoai môn để pha chế hoặc có thể dùng củ khoai môn tươi xay lấy nước sẽ nguyên chất hơn. 2 loại topping hợp với trà sữa khoai môn là trân châu đen và trân châu trắng.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa matcha đậu đỏ', 'https://jarvis.vn/uploaded/tra-sua-matcha-dau-do(1).jpg', 'Nếu trà sữa matcha là thức uống quá quen thuộc và phổ biến thì trà sữa matcha đậu đỏ lại là sản phẩm mới, thích hợp giải nhiệt dành cho mùa hè nắng nóng. Trà sữa Matcha đậu đỏ về cơ bản vẫn là trà sữa trà xanh với vị chát nhẹ của trà, vị thơm mát của matcha cùng với chút ngọt nhẹ của sữa tươi.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Oreo Chocolate Cream', 'https://jarvis.vn/wp-content/uploads/2019/05/oreo-cake-cream-3.png', 'Trong số các loại trà sữa phổ biến thì trà sữa Oreo Chocolate Cream là một loại trà sữa khá mới trong thời gian gần đây. Với những người hảo ngọt thì vị béo ngậy của cốc trà này sẽ làm bạn phải mê mẩn. Phần trà sữa thơm ngọt kết hợp cùng lớp bọt kem mặn, đặc biệt là mùi thơm quyến rũ của chocolate cùng bánh Oreo. Vị béo ngậy của lớp kem cùng vị ngọt của sữa trong trà sẽ không làm bạn cảm thấy quá ngán nhờ chút đắng mà chocolate cùng vụn bánh Oreo mang lại.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Pudding đậu đỏ', 'https://jarvis.vn/wp-content/uploads/2019/05/tra-sua-pudding-dau-do.png', 'Đây là loại trà sữa có topping gồm phần pudding trứng cùng với hạt đậu đỏ. Về phần trà sữa thì ngoài vị chát nhẹ cùng vị ngọt béo của sữa và trà thì điểm nhấn của cốc trà sữa này nằm ở phần pudding trứng cùng đậu đỏ phía trên. Pudding trứng mềm mại, có vị ngọt và thơm, pha chút béo ngậy của vani và có màu vàng óng ngọt ngào, đi kèm đó là những hạt đậu đỏ mềm nhuyễn cũng có vị ngọt nhưng lại khá bùi khi nhai. 2 nguyên liệu này kết hợp cùng trà sữa tạo thành trà sữa Pudding đậu đỏ thích hợp giải nhiệt mùa hè', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa trân châu đen và trà sữa trân châu trắng', 'https://jarvis.vn/uploaded/tra-sua-tran-chau-den-va-tra-sua-tran-chau-trang(1).jpg', 'Trà sữa trân châu đen và trà sữa trân châu trắng hẳn là 2 thức uống có mặt lâu nhất trong các menu tại các quán trà sữa cũng như các quán ăn vặt. Không phải tự nhiên mà 2 loại thức uống này vẫn tồn tại và được lòng nhiều người bên cạnh hàng loạt các sản phẩm trà sữa mới.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa sương sáo', 'https://jarvis.vn/uploaded/tra-suong-sao.jpg', 'Sương sáo, sương sa là những nguyên liệu phổ biến tại các tỉnh miền Nam và cực thích hợp cho những ngày hè nắng nóng. Sương sáo có tính giải nhiệt cao cùng với đó là nhiều công dụng tốt cho sức khỏe. Trà sữa sương sáo là sự kết hợp giữa trà sữa truyền thống với phần sương sáo mát lạnh.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Earl Grey', 'https://jarvis.vn/wp-content/uploads/2019/05/Earl-Grey-Bubble-Tea.png', 'Trà sữa Earl Grey hay còn gọi là Trà Bá Tước là một loại trà sữa độc đáo được làm từ trà xuất xứ từ nước Anh với công đoạn ướp khá cầu kỳ. Trà sữa Earl Grey có vị khá chát và đắng nhẹ, đặc biệt là mùi thơm rất đặc trưng mà không phải loại trà nào cũng có.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Caramel', 'https://jarvis.vn/uploaded/tra-sua-caramel.jpg', 'Caramel hẳn là một món ăn, một nguyên liệu phổ biến đặc biệt với giới trẻ. Chỉ riêng caramel cũng có thể kết hợp được với vô số nguyên liệu khác để tạo thành những món ăn ngon và hấp dẫn. Trà sữa caramel được làm từ trà sữa truyền thống kết hợp cùng lớp kem caramel thơm và béo ngậy.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa Cà phê', 'https://jarvis.vn/uploaded/tra-sua-ca-phe.jpg', 'Nhắc tới cà phê là nhắc tới một loại đồ uống truyền thống của người dân Việt Nam. Hương vị của cà phê có chút đắng, khác với vị đắng của chocolate. Đặc biệt thì cà phê cũng có mùi thơm quyến rũ mà chỉ cần ngửi cũng muốn được uống thử. Trà sữa cà phê hẳn là sự kết hợp khá độc đáo giữa 2 nguyên liệu tưởng chừng có ít sự liên quan này với nhau.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Lục trà sữa', 'https://jarvis.vn/wp-content/uploads/2019/05/luc-tra-sua.png', 'Lục trà sữa là một loại trà sữa phổ biến và có mặt hầu như các menu của các tiệm trà sữa. Lục trà sữa ngoài vị chát nhẹ thì còn có vị thơm nồng và ngọt ngọt của sữa. Lục trà sữa có nhiều sữa hơn so với hồng trà sữa.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Trà sữa cheese milk foam', 'https://jarvis.vn/uploaded/tra-sua-cheese-milk-foam.jpg', 'Trà sữa cheese milk foam là một loại đồ uống được giới trẻ đặc biệt yêu thích thời gian đổ lại đây. Ngoài vị trà sữa truyền thống thì cốc trà còn được thêm một lớp kem sữa béo béo, ngậy ngậy tràn mùi thơm.', 'Trà sữa', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('BÁNH MÌ CHÀ BÔNG PHÔ MAI', 'https://product.hstatic.net/1000075078/product/phomaichabong_1x1_bed88825466e4e53bde516ce1febdb56_large.jpg', 'Bạn không thể bỏ lỡ chiếc bánh với lớp phô mai vàng sánh mịn bên trong, được bọc ngoài lớp vỏ xốp mềm thơm lừng. Thêm lớp chà bông mằn mặn hấp dẫn bên trên.', 'Snack', '32000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Bánh mì que', 'https://product.hstatic.net/1000075078/product/banh_mi_que_40c05d95e9eb44e5997a4071b004ea97_large.jpg', 'Bánh mì que dài dài giòn giòn với lớp nhân thịt ngon hết xẩy', 'Snack', '12000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('BÔNG LAN TRỨNG MUỐI', 'https://product.hstatic.net/1000075078/product/trungmui1_9abf7c43946b44e9948dbac1eff95e40_large.jpg', 'Chắc chắn bạn sẽ không thể cưỡng lại chiếc bánh bông lan tơi xốp, mềm mịn, vị ngọt dịu kết hợp với trứng muối và chà bông đậm đà, cân bằng vị giác.', 'Snack', '29000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('cam tươi sấy dẻo', 'https://product.hstatic.net/1000075078/product/cam_vang_say_deo_79c5c1eb4de840f7936b6679c6e1f138_large.jpg', 'Cam tươi sấy khô dẻo dai, ngọt bùi và chua thanh đầy thú vị. Món ăn vặt \"healthy\" kích thích vị giác cho bạn khi buồn miệng.', 'Snack', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Croissant Trứng muối', 'https://product.hstatic.net/1000075078/product/photo_2020-11-10_17-41-51_6c3b1ba948354101b892644fb7aab5c6_large.jpg', 'Bánh croissant của Pháp được phủ lớp chà bông mặn mà và rắc bột trứng muối thơm thơm tạo nên một kiệt tác ẩm thực không thể tả', 'Snack', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Điều vàng mật ong', 'https://product.hstatic.net/1000075078/product/hat-dieu-mat-ong_472628eb00474362b4d2f9acbcb4d879_large.jpg', 'Ăn vặt cũng phải \"healthy\". Hạt điều nhiều dinh dưỡng, phủ lớp mật ong ngọt ngọt mặn mặn, ăn hoài ngon hoài, hứa luôn!', 'Snack', '30000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Gà Xé Lá Chanh', 'https://product.hstatic.net/1000075078/product/ga_xe_la_chanh_7fd28bb113bc40068f8c599f8c04669e_large.jpg', 'Thịt gà được xé tơi, vị mặn, ngọt và cay quyện nhau vừa chuẩn, thêm chút thơm thơm từ lá chanh sấy khô giòn giòn nữa thì cơn buồn miệng nào cũng sẽ bị xua tan.', 'Snack', '25000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Heo Sấy Xông Khói', 'https://product.hstatic.net/1000075078/product/heo_say_xong_khoi_1c4b202aac434634818939d3d2fd86bd_large.jpg', 'Thịt heo được ướp gia vị đậm đà - mặn, ngọt, chua, cay ngon ngất ngây. Chút \"mồi\" sừng sựt để cuộc hẹn hò không bao giờ chán.', 'Snack', '35000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mít sấy', 'https://product.hstatic.net/1000075078/product/mit-say_1x1_23736c9eef904141bf9311fdc4be2afa_large.jpg', 'Khi \"ăn vặt\" đã trở thành xu thế, thì sao có thể bỏ qua mít sấy cực ngon của Nhà. Mít sấy khô vàng ươm giòn rụm, khi ăn vẫn giữ nguyên được vị ngọt lịm của mít tươi. Đến Nhà trò chuyện rôm rã và thưởng thức mít sấy để cho câu chuyện thêm giòn thêm vui ngay thôi!', 'Snack', '20000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mochi Kem Chocolate', 'https://product.hstatic.net/1000075078/product/mochi-chocolate_24bddab1f9c64658a4052480e5e4f1ba_large.jpg', 'Cảm nhận hương vị Á Âu từ lớp vỏ nếp sô cô la ngọt dẻo, đến lớp kem lạnh và sốt sô cô la bên trong. Cắn 1 miếng là mê ngay.', 'Snack', '19000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mochi Kem Matcha', 'https://product.hstatic.net/1000075078/product/mochi-matcha_7fb346641da54f9cb410a59b5ae716f4_large.jpg', 'Vừa quen vừa lạ từ vỏ bánh mềm dẻo, đến lớp kem lạnh và nhân trà xanh đậm vị, gọi 1 chiếc Mochi cho ngày thiệt tươi nha', 'Snack', '19000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mochi Kem Xoài', 'https://product.hstatic.net/1000075078/product/mochi-mango_7575626c13c841df910ff9ef5ecd4f24_large.jpg', 'Bạn sẽ thích thú với hương vị chua chua ngọt ngọt từ sự kết hợp giữa nhân kem lạnh và sốt xoài. Bao bọc bởi lớp vỏ nếp mềm dẻo. Gọi Mocchi Xoài cho ngày dài thiệt tươi.', 'Snack', '19000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mousse Gấu Chocolate', 'https://product.hstatic.net/1000075078/product/gau_1x1_8b308db3a67a47688ff0d69a8c5615bf_large.jpg', 'Với vẻ ngoài đáng yêu và hương vị ngọt ngào, thơm béo nhất định bạn phải thử ít nhất 1 lần.', 'Snack', '39000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mousse Matcha', 'https://product.hstatic.net/1000075078/product/matcha_178bdeeb1f9b47ea9f782048eb145f49_large.jpg', 'Khám phá từng tầng vị trà xanh thơm lừng xen kẽ lớp kéo béo dịu với đậu đỏ.', 'Snack', '29000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mousse Passion Cheese', 'https://product.hstatic.net/1000075078/product/chanhday_1x1_da85397059a149e1b886237434f7ce48_large.jpg', 'Tận hưởng chiếc bánh mát lạnh với sự kết hợp hoàn hảo của vị béo ngậy của nhân kem phô mai, cân bằng với vị chua thanh từ chanh dây.', 'Snack', '29000', 'Bình thường');
INSERT INTO `coffeeblend`.`product` (`product_name`, `url`, `description`, `type`, `price`,`status`) VALUES ('Mousse Tiramisu', 'https://product.hstatic.net/1000075078/product/tira_1x1_eb865751a24e4520be32a56129dd7a2d_large.jpg', 'Hương vị dễ ghiền được tạo nên bởi chút đắng nhẹ của cà phê, lớp kem trứng béo ngọt dịu hấp dẫn.', 'Snack', '32000', 'Bình thường');
