drop database if exists CoffeeBlend;
create database CoffeeBlend;
use CoffeeBlend;

CREATE TABLE `type` (
    `type_id` INT PRIMARY KEY AUTO_INCREMENT,
    `type_name` VARCHAR(255)
);

CREATE TABLE `status` (
    `status_id` INT PRIMARY KEY AUTO_INCREMENT,
    `status_name` VARCHAR(255)
);

CREATE TABLE `Product` (
    `product_id` INT PRIMARY KEY AUTO_INCREMENT,
    `product_name` VARCHAR(255),
    `image` VARCHAR(255),
    url VARCHAR(255),
    `description` VARCHAR(1000),
    `price` FLOAT,
    `type` VARCHAR(255),
    `status` VARCHAR(100)
);

CREATE TABLE `ProductRating` (
    `id` INT PRIMARY KEY AUTO_INCREMENT,
    `product_id` INT,
    `rating` INT,
    `date_rated` DATETIME
);


CREATE TABLE `CustomerDetail` (
    `customer_id` INT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255),
    `email` VARCHAR(255),
    `password` VARCHAR(255),
    `address` VARCHAR(255),
    `phone` INT,
    `point` INT,
    `created_time` DATETIME,
    `last_login` DATETIME,
    `enabled` TINYINT,
    `auth_provider` VARCHAR(255)
);
CREATE TABLE `Invoice` (
    `id` INT PRIMARY KEY AUTO_INCREMENT,
    `invoice_status` VARCHAR(255),
    customer_id INT,
    customer_name VARCHAR(255),
    customer_address VARCHAR(255),
    customer_phone VARCHAR(10),
    total_cart_price FLOAT,
    `day_created` DATE,
    day_updated DATETIME
   --  FOREIGN KEY (customer_id)
--         REFERENCES coffeeblend.customerdetail (customer_id)
);

CREATE TABLE `OrderDetail` (
    `id` INT PRIMARY KEY AUTO_INCREMENT,
    `product_id` INT,
    `invoice_id` INT,
    `quantity` INT,
    `product_price` FLOAT,
    `total_price` FLOAT,
    CONSTRAINT `order_details_invoice_fk` FOREIGN KEY (`invoice_id`)
        REFERENCES `coffeeblend`.`invoice` (`id`),
    CONSTRAINT `order_details_prod_fk` FOREIGN KEY (`product_id`)
        REFERENCES `coffeeblend`.`product` (`product_id`)
);

CREATE TABLE `InvoiceStatus` (
    `status_id` INT PRIMARY KEY AUTO_INCREMENT,
    `status_name` VARCHAR(255)
);



CREATE TABLE `RoleDetail` (
    `role_id` INT PRIMARY KEY AUTO_INCREMENT,
    `role_name` VARCHAR(255)
);

CREATE TABLE `admindetail` (
    `admin_id` INT(11) PRIMARY KEY AUTO_INCREMENT NOT NULL,
    `admin_fullname` VARCHAR(100) NOT NULL,
    `admin_email` VARCHAR(45) NOT NULL,
    `admin_user` VARCHAR(50),
    `admin_pass` VARCHAR(64) NOT NULL,
    `admin_address` VARCHAR(150),
    `admin_phone` VARCHAR(10),
    `admin_createdTime` DATE,
    `admin_last_login` DATE,
    `role_name` VARCHAR(25),
    `admin_role` INT,
    `admin_image` VARCHAR(255),
    `enabled` TINYINT,
    FOREIGN KEY (admin_role)
        REFERENCES roledetail (role_id)
);


CREATE TABLE `UserRole` (
    admin_id INT NOT NULL,
    role_id INT NOT NULL,
    CONSTRAINT fk_admin_id FOREIGN KEY (admin_id)
        REFERENCES admindetail (admin_id),
    CONSTRAINT fk_role_id FOREIGN KEY (role_id)
        REFERENCES roledetail (role_id)
);

CREATE TABLE `ProductAndType` (
    product_id INT NOT NULL,
    type_id INT NOT NULL,
    CONSTRAINT fk_product_id FOREIGN KEY (product_id)
        REFERENCES product (product_id),
    CONSTRAINT fk_type_id FOREIGN KEY (type_id)
        REFERENCES type (type_id)
);

CREATE TABLE `chatRoom` (
    `room_id` INT PRIMARY KEY AUTO_INCREMENT,
    `created_time` DATETIME
);

CREATE TABLE `Message` (
    `message_id` INT PRIMARY KEY AUTO_INCREMENT,
    `room_id` INT,
    `customer_id` INT,
    `admin_id` INT,
    `customer_message` VARCHAR(255),
    `admin_message` VARCHAR(255)
);

